abstract contract IRSTypes {
    struct IRS {
        address fixedInterestPayer;
        address floatingInterestPayer;
        uint8 ratesDecimals;
        uint8 status;
        uint256 swapRate;
        uint256 spread;
        address assetContract;
        uint256 notionalAmount;
        uint256 paymentFrequency;
        uint256 startingDate;
        uint256 maturityDate;
        uint256 benchmark;
    }
}

