contracts/BondData.sol.content:
// SPDX-License-Identifier: CC0-1.0
pragma solidity ^0.8.0;

abstract contract BondData {
    struct Bond {
        string isin;
        string name;
        string symbol;
        address currency;
        uint256 denomination;
        uint256 issueVolume;
        uint256 couponRate;
        uint8 couponType;
        uint256 couponFrequency;
        uint256 issueDate;
        uint256 maturityDate;
    }

    struct Deal {
        string dealID;
        string prospectusURI;
        address issuerAddress;
        uint256 debtAmount;
        uint256 denomination;
        uint256 couponRate;
        uint256 couponFrequency;
        uint256 maturityDate;
        uint256 index;
        address currency;
        uint8 couponType;
        DealStatus status;
    }

    struct Issuer {
        string documentURI;
        string name;
        string country;
        string issuerType;
        string creditRating;
        uint256 carbonCredit;
        address walletAddress;
        StakeHolderStatus status;
        uint256 index;
        string logoURI;
    }

    struct Investor {
        string name;
        string country;
        string investorType;
        address walletAddress;
        StakeHolderStatus status;
        uint256 index;
    }

    struct IssueData {
        address issuerWalletAddress;
        string countryOfIssuance;
    }

    struct Investment {
        uint256 amount;
        bool hasInvested;
        uint256 index;
    }

    struct DealInvestment {
        address investor;
        uint256 amount;
    }

    struct CouponPaymentReceipt {
        uint256 issueVolume;
        uint256 couponRate;
        uint256 interestPaid;
    }

    struct Listing {
        string dealID;
        address owner;
        uint256 amount;
        uint256 price;
        uint256 index;
        uint256 listingTime;
    }

    error InvalidInvestorAddress(address investor);
    error InvalidDealStatus(string dealID);
    error InvalidAmount(uint256 amount);

    enum BondStatus {ISSUED, REDEEMED}
    enum DealStatus {UNDEFINED, SUBMITTED, APPROVED, REJECTED, ISSUED, REDEEMED}
    enum StakeHolderStatus {UNDEFINED, SUBMITTED, APPROVED, REJECTED}
}

contracts/BondStorage.sol.content:
// SPDX-License-Identifier: CC0-1.0
pragma solidity ^0.8.0;

import "./BondData.sol";
import "./Registry/IIdentityRegistry.sol";
import "./Topos/interfaces/IToposBank.sol";

contract BondStorage {
    mapping(string => BondData.IssueData) public issueData;
    mapping(string => BondData.Bond) public bonds;
    mapping(address => uint256) principals;
    mapping(address => bool) public isInvestor;
    mapping(address => uint256) public investorsIDs;
    mapping(address => mapping(address => uint256)) approvals;

    string public dealID;
    address public bondManager;
    address public toposBankContract;

    BondData.BondStatus public bondStatus;

    BondData.DealInvestment[] listOfInvestors;

    modifier onlyBondManager {
        require(msg.sender == bondManager, "ONLY_BOND_MANAGER");
        _;
    }

    modifier onlyToposBankContract {
        require(
            msg.sender == toposBankContract,
            "ONLY_TOPOS_BANK_CONTRACT"
        );
        _;
    }

    modifier mustBeApproved(address _user) {
        (, , address registry, ,) = IToposBank(toposBankContract).getContracts();

        require(
            IIdentityRegistry(registry).isVerified(_user),
            "ACCOUNT_NOT_AAPROVED"
        );
        _;
    }
}

contracts/BondTopos.sol.content:
// SPDX-License-Identifier: CC0-1.0
pragma solidity ^0.8.0;

import "./IERC7092.sol";
import "./BondStorage.sol";
import "./BondData.sol";
import "./Topos/interfaces/IBonds.sol";
import "./Topos/interfaces/IToposBank.sol";

contract BondTopos is IERC7092, BondStorage, IBonds {
    constructor(
        string memory _dealID,
        address _issuerWalletAddress,
        address _toposBankContract,
        string memory _countryOfIssuance
    ) {
        dealID = _dealID;
        bondManager = msg.sender;

        issueData[_dealID].issuerWalletAddress = _issuerWalletAddress;
        issueData[_dealID].countryOfIssuance = _countryOfIssuance;
        toposBankContract = _toposBankContract;
    }

    /**
    * @notice Issues bonds to investors
    * @param _bond Bond Struct see `BondData.sol`
    */
    function issue(
        BondData.Bond calldata _bond
    ) external onlyToposBankContract {
        _issue(_bond);
    }

    /**
    * @notice Redeems bonds
    */
    function redeem() external onlyToposBankContract {
        _redeem();
    }

    function isin() external view returns(string memory) {
        return bonds[dealID].isin;
    }

    function name() external view returns(string memory) {
        return bonds[dealID].name;
    }

    function symbol() external view returns(string memory) {
        return bonds[dealID].symbol;
    }

    function currency() external view returns(address) {
        return bonds[dealID].currency;
    }

    function denomination() external view returns(uint256) {
        return bonds[dealID].denomination;
    }

    function issueVolume() external view returns(uint256) {
        return bonds[dealID].issueVolume;
    }

    function totalSupply() external view returns(uint256) {
        return bonds[dealID].issueVolume / bonds[dealID].denomination;
    }

    function couponRate() external view returns(uint256) {
        uint256 _benchmark = IToposBank(toposBankContract).getBenchmark();
        uint256 _couponRate = bonds[dealID].couponRate;
        uint256 _couponType = bonds[dealID].couponType;

        if(_couponType == 2) {
            return _couponRate + _benchmark;
        } else {
           return _couponRate; 
        }
    }

    function couponType() external view returns(uint8) {
        return bonds[dealID].couponType;
    }

    function couponFrequency() external view returns(uint256) {
        return bonds[dealID].couponFrequency;
    }

    function issueDate() external view returns(uint256) {
        return bonds[dealID].issueDate;
    }

    function maturityDate() external view returns(uint256) {
        return bonds[dealID].maturityDate;
    }

    function principalOf(address _account) external view returns(uint256) {
        return principals[_account];
    }

    function balanceOf(address _account) external view returns(uint256) {
        return principals[_account] / bonds[dealID].denomination;
    }

    function allowance(address _owner, address _spender) external view returns(uint256) {
        return approvals[_owner][_spender];
    }

    function approve(
        address _spender,
        uint256 _amount
    ) external returns(bool) {
        address _owner = tx.origin;

        _approve(_owner, _spender, _amount);

        return true;
    }

    function decreaseAllowance(
        address _spender,
        uint256 _amount
    ) external {
        address _owner = tx.origin;

        _decreaseAllowance(_owner, _spender, _amount);
    }

    function transfer(
        address _to,
        uint256 _amount,
        bytes calldata _data
    ) external mustBeApproved(_to) returns(bool) {
        address _from = msg.sender;

        _transfer(_from, _to, _amount, _data);

        return true;
    }

    function transferFrom(
        address _from,
        address _to,
        uint256 _amount,
        bytes calldata _data
    ) external mustBeApproved(_to) returns(bool) {
        address _spender = msg.sender;

        _spendAllowance(_from, _spender, _amount);
        _transfer(_from, _to, _amount, _data);

        return true;
    }

    function getListOfInvestors() external view returns(BondData.DealInvestment[] memory) {
        return listOfInvestors;
    }

    function bankContract() external view returns(address) {
        return toposBankContract;
    }

    function _issue(BondData.Bond calldata _bond) internal virtual {
        uint256 _totalAmountInvested = IToposBank(toposBankContract).getTotalAmounInvested(dealID);
        require(_bond.issueVolume ==  _totalAmountInvested);

        BondData.DealInvestment[] memory _dealInvestment = IToposBank(toposBankContract).getDealInvestment(dealID);

        uint256 _denomination = bonds[dealID].denomination;
        uint256 volume;

        for(uint256 i; i < _dealInvestment.length; i++) {
            address investor = _dealInvestment[i].investor;
            uint256 principal = _dealInvestment[i].amount;

            require(investor != address(0));
            require(
                principal != 0 && (principal * _denomination) * _denomination == 0
            );

            volume += principal;
            principals[investor] = principal;
            isInvestor[investor] = true;
            investorsIDs[investor] = i;

            listOfInvestors.push(
                BondData.DealInvestment({
                    investor: investor,
                    amount: principal
                })
            );
        }

        bonds[dealID] = _bond;
        bonds[dealID].issueDate = block.timestamp;
        bondStatus = BondData.BondStatus.ISSUED;

        require(bonds[dealID].maturityDate > block.timestamp);
        require(volume == _totalAmountInvested);
    }

    function _redeem() internal virtual {
        require(bondStatus == BondData.BondStatus.ISSUED);
        require(block.timestamp > bonds[dealID].maturityDate);

        bondStatus = BondData.BondStatus.REDEEMED;

        BondData.DealInvestment[] memory _dealInvestment = IToposBank(toposBankContract).getDealInvestment(dealID);

        for(uint256 i; i < _dealInvestment.length; i++) {
            if(principals[_dealInvestment[i].investor] != 0) {
                principals[_dealInvestment[i].investor] = 0;
                listOfInvestors[i].amount = 0;
            }
        }
    }

    function _approve(
        address _owner,
        address _spender,
        uint256 _amount
    ) internal virtual {
        require(_owner != address(0));
        require(_spender != address(0));
        require(_amount > 0);

        uint256 principal = principals[_owner];
        uint256 approval = approvals[_owner][_spender];
        uint256 _denomination = bonds[dealID].denomination;
        uint256 _maturityDate = bonds[dealID].maturityDate;
        uint256 balance = principal / _denomination;

        require(block.timestamp < _maturityDate, "matured");
        require(_amount <= balance);
        require((_amount * _denomination) % _denomination == 0);

        approvals[_owner][_spender] = approval + _amount;

        emit Approval(_owner, _spender, _amount);
    }

    function _decreaseAllowance(
        address _owner,
        address _spender,
        uint256 _amount
    ) internal virtual {
        require(_owner != address(0));
        require(_spender != address(0));
        require(_amount > 0);

        uint256 approval = approvals[_owner][_spender];
        uint256 _denomination = bonds[dealID].denomination;
        uint256 _maturityDate = bonds[dealID].maturityDate;

        require(block.timestamp < _maturityDate);
        require(_amount <= approval);
        require((_amount * _denomination) % _denomination == 0);

        approvals[_owner][_spender] = approval - _amount;

        emit Approval(_owner, _spender, _amount);
    }

    function _transfer(
        address _from,
        address _to,
        uint256 _amount,
        bytes calldata _data
    ) internal virtual {
        require(_from != address(0));
        require(_to != address(0));
        require(_amount > 0);

        uint256 principal = principals[_from];
        uint256 _denomination = bonds[dealID].denomination;
        uint256 _maturityDate = bonds[dealID].maturityDate;
        uint256 balance = principal / _denomination;

        require(block.timestamp < _maturityDate, "matured");
        require(_amount <= balance);
        require((_amount * _denomination) % _denomination == 0);

        _beforeBondTransfer(_from, _to, _amount, _data);

        uint256 principalTo = principals[_to];
        unchecked {
            uint256 principalToTransfer = _amount * _denomination;

            principals[_from] = principal - principalToTransfer;
            principals[_to] = principalTo + principalToTransfer;

            if(!isInvestor[_to]) {
                isInvestor[_to] = true;

                listOfInvestors[investorsIDs[_from]].amount = principals[_from];

                listOfInvestors.push(
                    BondData.DealInvestment(
                        {
                            investor: _to,
                            amount: principals[_to]
                        }
                    )
                );

                investorsIDs[_to] = listOfInvestors.length - 1;
            } else {
                listOfInvestors[investorsIDs[_from]].amount = principals[_from];
                listOfInvestors[investorsIDs[_to]].amount = principals[_to];
            }
        }

        _afterBondTransfer(_from, _to, _amount, _data);

        emit Transfer(_from, _to, _amount);
    }

    function _spendAllowance(
        address _from,
        address _spender,
        uint256 _amount
    ) internal virtual {
        uint256 currentApproval = approvals[_from][_spender];
        require(_amount <= currentApproval);

        unchecked {
            approvals[_from][_spender] = currentApproval - _amount;
        }
    }

    function _beforeBondTransfer(address _from, address _to, uint256 _amount, bytes calldata _data) internal virtual {}

    function _afterBondTransfer(address _from, address _to, uint256 _amount, bytes calldata _data) internal virtual {}
}

contracts/IERC7092.sol.content:
// SPDX-License-Identifier: CC0-1.0
pragma solidity ^0.8.0;

/**
* @title ERC-7092 Financial Bonds Standard
*/
interface IERC7092 {
    /**
    *  @notice Returns the bond isin
    */
    function isin() external view returns(string memory);

    /**
    * @notice Returns the bond name
    */
    function name() external view returns(string memory);

    /**
    * @notice Returns the bond symbol
    *         It is RECOMMENDED to represent the symbol as a combination of the issuer issuer's shorter name and the maturity date
    *         Ex: If a company named Green Energy issues bonds that will mature on october 25, 2030, the bond symbol could be `GE30` or `GE2030` or `GE102530`
    */
    function symbol() external view returns(string memory);

    /**
    * @notice Returns the bond currency. This is the contract address of the token used to make payments and return the bond principal
    */
    function currency() external view returns(address);

    /**
    * @notice Returns the bond denomination. This is the minimum amount in which the bonds may be issued. It MUST be expressed in units of the principal currency.
    *         ex: If the denomination is set at 1,000, and the currency is USDC, then the bond denomination is equivalent to 1,000 USDC.
    */
    function denomination() external view returns(uint256);

    /**
    * @notice Returns the issue volume, which represents the total debt amount. It is RECOMMENDED to express the issue volume in terms of the denomination unit.
    */
    function issueVolume() external view returns(uint256);

    /**
    * @notice Returns the bond interest rate. It is RECOMMENDED to express the interest rate in basis points (bps).
    *         1 basis point = 0.01% = 0.0001
    *         ex: if interest rate = 5%, then coupon() => 500 basis points
    */
    function couponRate() external view returns(uint256);

    /**
    * @notice Returns the coupon type
    *         For example, 0 can denote Zero coupon, 1 can denote Fixed Rate, 2 can denote Floating Rate, and so on
    *
    * OPTIONAL - interfaces and other contracts MUST NOT expect these values to be present. The method is used to improve usability.
    */
    function couponType() external view returns(uint8);

    /**
    * @notice Returns the coupon frequency, i.e. the number of times coupons are paid in a year.
    *
    * OPTIONAL - interfaces and other contracts MUST NOT expect these values to be present. The method is used to improve usability.
    */
    function couponFrequency() external view returns(uint256);

    /**
    * @notice Returns the date when bonds were issued to investors. This is a Unix Timestamp similar the one returned by block.timestamp
    */
    function issueDate() external view returns(uint256);

    /**
    * @notice Returns the bond maturity date, i.e, the date when the principal is repaid. This is a Unix Timestamp similar the one returned by block.timestamp
    *         The maturity date MUST be greater than the issue date
    */
    function maturityDate() external view returns(uint256);

    /**
    * @notice Returns the principal of an account. It is RECOMMENDED to express the principal in the bond currency unit (e.g., USDC, DAI, etc)
    * @param _account account address
    */
    function principalOf(address _account) external view returns(uint256);

    /**
    * @notice Returns the number of tokens that the `_spender` account has been authorized by the `_owner` to manage
    * @param _owner the bondholder address
    * @param _spender the address that has been authorized by the bondholder
    */
    function allowance(address _owner, address _spender) external view returns(uint256);

    /**
    * @notice Authorizes `_spender` account to manage  a specified `_amount`of the bondholder's tokens
    * @param _spender the account to be authorized by the bondholder
    * @param _amount amount of bond tokens to approve
    */
    function approve(address _spender, uint256 _amount) external returns(bool);

    /**
    * @notice Decreases the allowance granted to `_spender` by `_amount`
    * @param _spender the address to be authorized by the bondholder
    * @param _amount amount of bond tokens to remove from allowance
    */
    function decreaseAllowance(address _spender, uint256 _amount) external;

    /**
    * @notice Transfers `_amount` bonds to the address `_to`. Additionally, this method enables to attach data to the token being transferred
    * @param _to the address to send bonds to
    * @param _amount amount of bond tokens to transfer
    * @param _data additional information provided by the token holder
    */
    function transfer(address _to, uint256 _amount, bytes calldata _data) external returns(bool);

    /**
    * @notice Transfers `_amount` bonds from an account that has previously authorized the caller through the `approve` function
    *         This methods also allows to attach data to the token that is being transferred
    * @param _from the bondholder address
    * @param _to the address to transfer bonds to
    * @param _amount amount of bond tokens to transfer.
    * @param _data additional information provided by the token holder
    */
    function transferFrom(address _from, address _to, uint256 _amount, bytes calldata _data) external returns(bool);

    /**
    * @notice MUST be emitted when bond tokens are transferred, issued or redeemed, with the exception being during contract creation
    * @param _from bondholder account
    * @param _to account to transfer bonds to
    * @param _amount amount of bond tokens to be transferred
    */
    event Transfer(address _from, address _to, uint256 _amount);

    /**
    * @notice MUST be emitted when an account is approved to spend tokens or when the allowance is decreased
    * @param _owner bondholder account
    * @param _spender the account to be allowed to spend bonds
    * @param _amount amount of bond tokens allowed by _owner to be spent by `_spender`
    *        Or amount of bond tokens to decrease allowance from `_spender`
    */
    event Approval(address _owner, address _spender, uint256 _amount);
}

