pragma solidity ^0.8.19;

interface IERC20 {
    event Transfer(address indexed from, address indexed to, uint256 value);
    event Approval(address indexed owner, address indexed spender, uint256 value);

    function name() external view returns (string memory);
    function symbol() external view returns (string memory);
    function totalSupply() external view returns (uint256);
    function balanceOf(address account) external view returns (uint256);
    function allowance(address owner, address spender) external view returns (uint256);
    function approve(address spender, uint256 amount) external returns (bool);
    function transfer(address to, uint256 amount) external returns (bool);
    function transferFrom(address from, address to, uint256 amount) external returns (bool);
    function mint(address account, uint256 amount) external returns(bool);
    function burn(address account, uint256 amount) external returns (bool);
}

contracts/Topos/Exchange/Exchange.sol.content:
// SPDX-License-Identifier: CC0-1.0
pragma solidity ^0.8.0;

import "./ExchangeStorage.sol";

contract Exchange is ExchangeStorage {
    constructor(
        address _bankContract,
        address _bondCallContract
    ) {
        owner = msg.sender;
        bankContract = _bankContract;
        bondCallContract = _bondCallContract;
    }

    /**
    * @notice Lists bonds on Exchange
    * @param _dealID bond deal ID
    * @param _amount amount of bonds to list
    * @param _price the price bonds should be sold at
    */
    function listBonds(
        string memory _dealID,
        uint256 _amount,
        uint256 _price
    ) external {
        address bondContract = IToposBank(bankContract).getDealBondContract(_dealID);

        uint256 allowance = IERC7092(bondContract).allowance(
            msg.sender,
            address(this)
        );

        require(_amount <= allowance, "allowance");
        require(_amount > 0, "invalid amount");

        IExchangeBondsStorage(exchangeBondsStorage).depositBonds(
            msg.sender,
            _dealID,
            _amount,
            _price
        );

        IERC7092(bondContract).transferFrom(
            msg.sender,
            exchangeBondsStorage,
            _amount,
            bytes('0x0')
        );

        emit BondsListed(_dealID, msg.sender, _amount, _price);
    }

    /**
    * @notice Unlists bonds from exchange
    * @param _dealID bond deal ID
    */
    function unlistBonds(string memory _dealID) external {
        IExchangeBondsStorage(exchangeBondsStorage).cancelBonds(
            _dealID,
            msg.sender
        );

        emit BondsCanceled(_dealID, msg.sender);
    }

    /**
    * @notice Updates the price of a listing
    * @param _dealID bond deal ID
    * @param _newPrice new price
    */
    function updateDealPrice(
        string memory _dealID,
        uint256 _newPrice
    ) external {
        require(_newPrice > 0, "invalid amount");

        IExchangeBondsStorage(exchangeBondsStorage).updatePrice(
            _dealID,
            msg.sender,
            _newPrice
        );

        emit PriceUpdated(_dealID, msg.sender, _newPrice);
    }

    /**
    * Increases the amount of bonds listed
    * @param _dealID bond deal ID
    * @param _amountToAdd amount of tokens to add to the listing
    */
    function increaseListingAmount(
        string memory _dealID,
        uint256 _amountToAdd
    ) external {
        address bondContract = IToposBank(bankContract).getDealBondContract(_dealID);

        require(_amountToAdd > 0, "invalid amount");

        IExchangeBondsStorage(exchangeBondsStorage).increaseAmount(
            _dealID,
            msg.sender,
            _amountToAdd
        );

        uint256 allowance = IERC7092(bondContract).allowance(
            msg.sender,
            address(this)
        );

        require(_amountToAdd <= allowance, "allowance");

        IERC7092(bondContract).transferFrom(
            msg.sender,
            exchangeBondsStorage,
            _amountToAdd,
            bytes('0x0')
        );

        emit AmountIncreased(_dealID, msg.sender, _amountToAdd);
    }

    /**
    * @notice Buys bonds in secondary market
    * @param _dealID bond deal ID
    * @param _seller seller account address
    * @param _amount amount of bonds to buy
    */
    function buyBonds(
        string memory _dealID,
        address _seller,
        uint256 _amount
    ) external {
        BondData.Listing memory listing = IExchangeBondsStorage(exchangeBondsStorage).getInvestorListing(_seller, _dealID);
        address bondContract = IToposBank(bankContract).getDealBondContract(_dealID);
        address currency = IERC7092(bondContract).currency();
        uint256 price = listing.price;
        uint256 payment = _amount * price * 1 ether;
        uint256 buyerBalance = IERC20(currency).balanceOf(msg.sender);

        require(buyerBalance >= payment, "balance");

        IExchangeBondsStorage(exchangeBondsStorage).buy(
            _dealID,
            _seller,
            msg.sender,
            _amount
        );

        IERC20(currency).transferFrom(msg.sender, _seller, payment);

        emit BondBought(_dealID, _seller, msg.sender, _amount);
    }

    function setExchangeBondsStorage(
        address _exchangeBondsStorage
    ) external onlyOwner {
        require(_exchangeBondsStorage != address(0));

        exchangeBondsStorage = _exchangeBondsStorage;
    }
}

contracts/Topos/Exchange/ExchangeBondsSt.content:
// SPDX-License-Identifier: CC0-1.0
pragma solidity ^0.8.0;

import "../../IERC7092.sol";
import "../../BondData.sol";
import "../interfaces/IToposBank.sol";
import "./IExchangeBondsStorage.sol";

contract ExchangeBondsStorage is IExchangeBondsStorage {
    mapping(address => mapping(string => BondData.Listing)) public investorListing;

    address public bankContract;
    address public exchangeContract;
    address public bondCallContract;

    BondData.Listing[] dealListed;

    constructor(
        address _bankContract,
        address _exchangeContract,
        address _bondCallContract
    ) {
        bankContract = _bankContract;
        exchangeContract = _exchangeContract;
        bondCallContract = _bondCallContract;
    }

    modifier onlyExchangeContract {
        require(
            msg.sender == exchangeContract,
            "Only Exchange"
        );
        _;
    }

    function depositBonds(
        address _seller,
        string memory _dealID,
        uint256 _amount,
        uint256 _price
    ) external onlyExchangeContract {
        investorListing[_seller][_dealID] = BondData.Listing({
            dealID:  _dealID,
            owner: _seller,
            amount: _amount,
            price: _price,
            index: dealListed.length,
            listingTime: block.timestamp
        });

        dealListed.push(investorListing[_seller][_dealID]);
    }

    function cancelBonds(
        string memory _dealID,
        address _seller
    ) external onlyExchangeContract {
        address bondContract = IToposBank(bankContract).getDealBondContract(_dealID);

        require(
            _seller == investorListing[_seller][_dealID].owner,
            "invalid seller"
        );

        uint256 amount = investorListing[_seller][_dealID].amount;
        uint256 index = investorListing[_seller][_dealID].index;

        require(amount > 0, "invalid amount");

        investorListing[_seller][_dealID].amount = 0;
        investorListing[_seller][_dealID].price = 0;
        investorListing[_seller][_dealID].listingTime = 0;

        dealListed[index] = investorListing[_seller][_dealID];

        IERC7092(bondContract).transfer(
            _seller,
            amount,
            bytes('0x0')
        );
    }

    function updatePrice(
        string memory _dealID,
        address _seller,
        uint256 _newPrice
    ) external onlyExchangeContract {
        uint256 previousPrice = investorListing[_seller][_dealID].price;
        uint256 index = investorListing[_seller][_dealID].index;
        uint256 amount = investorListing[_seller][_dealID].amount;
        address seller = investorListing[_seller][_dealID].owner;

        address bondContract = IToposBank(bankContract).getDealBondContract(_dealID);
        uint256 maturityDate = IERC7092(bondContract).maturityDate();

        require(seller == _seller, "invalid seller");
        require(amount > 0, "invalid amount");
        require(_newPrice != previousPrice, "unchanged price");
        require(block.timestamp < maturityDate, "bonds matured");

        investorListing[_seller][_dealID].price = _newPrice;
        dealListed[index] = investorListing[_seller][_dealID];
    }

    function increaseAmount(
        string memory _dealID,
        address _seller,
        uint256 _amountToAdd
    ) external onlyExchangeContract {
        uint256 index = investorListing[_seller][_dealID].index;
        uint256 previousAmount = investorListing[_seller][_dealID].amount;
        address seller = investorListing[_seller][_dealID].owner;

        require(seller == _seller, "invalid seller");
        require(previousAmount > 0, "invalid amount");

        investorListing[_seller][_dealID].amount = previousAmount + _amountToAdd;
        dealListed[index] = investorListing[_seller][_dealID];
    }

    function buy(
        string memory _dealID,
        address _seller,
        address _buyer,
        uint256 _amount
    ) external onlyExchangeContract {
        address bondContract = IToposBank(bankContract).getDealBondContract(_dealID);

        uint256 index = investorListing[_seller][_dealID].index;
        uint256 amount = investorListing[_seller][_dealID].amount;
        address seller = investorListing[_seller][_dealID].owner;

        require(seller == _seller, "invalid seller");
        require(amount > 0 && amount >= _amount, "invalid amount");

        // If all bonds are bought, set to zero listing params
        if (amount == _amount) {
            investorListing[_seller][_dealID].amount = 0;
            investorListing[_seller][_dealID].price = 0;
            investorListing[_seller][_dealID].listingTime = 0;
        } else {
            investorListing[_seller][_dealID].amount = amount - _amount;
        }

        dealListed[index] = investorListing[_seller][_dealID];

        IERC7092(bondContract).transfer(_buyer, _amount, bytes('0x0'));
    }

    /**
    * @notice Returns the array of all listings
    */
    function getDealsListed() external view returns(BondData.Listing[] memory) {
        return dealListed;
    }

    function getInvestorListing(
        address _seller,
        string memory _dealID
    ) external view returns(BondData.Listing memory) {
        return investorListing[_seller][_dealID];
    }
}

contracts/Topos/Exchange/ExchangeStorage.content:
// SPDX-License-Identifier: CC0-1.0
pragma solidity ^0.8.0;

import "../../IERC7092.sol";
import "../../BondData.sol";
import "../interfaces/IToposBank.sol";
import "../../tests/tokens/IERC20.sol";
import "./IExchangeBondsStorage.sol";

contract ExchangeStorage {
    address public owner;

    address public bankContract;
    address public bondCallContract;
    address public exchangeBondsStorage;

    modifier onlyOwner {
        require(msg.sender == owner, "Only owner");
        _;
    }

    event BondsListed(string dealID, address seller, uint256 amount, uint256 price);
    event BondsCanceled(string dealID, address seller);
    event PriceUpdated(string dealID, address seller, uint256 newPrice);
    event BondBought(string dealID, address seller, address buyer, uint256 amount);
    event AmountIncreased(string dealID, address seller, uint256 amountToAdd);
}

