// SPDX-License-Identifier: CC0-1.0
pragma solidity ^0.8.0;

interface IIRS {
    function swap() external;
    function terminateSwap() external;
    function setBenchmark(uint256 _newBenchmark) external;
}

contracts/Topos/IRS/IRSStorage.sol.content:
// SPDX-License-Identifier: CC0-1.0
pragma solidity ^0.8.0;

import "./IRSTypes.sol";

abstract contract IRSStorage {
    IRSTypes.IRS irs;
    address public fixedPayerContract;
    address public floatingPayerContract;
    address public owner;
    address public toposBankContract;
    uint8 public isActive;
    uint8 public numberOfSwaps;
    uint8 public swapCount;

    event SetBenchmark(uint256 oldBenchmark, uint256 newBenchmark);

    modifier onlyOwner {
        require(msg.sender == owner, "Not owner");
        _;
    }

    modifier mustBeActive {
        require(isActive == 1, "Not Active");
        _;
    }

    modifier onlyToposBank {
        require(msg.sender == toposBankContract, "Only Bank Contract");
        _;
    }
}

contracts/Topos/IRS/IRSTypes.sol.content:
// SPDX-License-Identifier: CC0-1.0
pragma solidity ^0.8.0;

abstract contract IRSTypes {
    struct IRS {
        address fixedInterestPayer;
        address floatingInterestPayer;
        uint8 ratesDecimals;
        uint256 swapRate;
        uint256 spread;
        address assetContract;
        uint256 notionalAmount;
        uint256 paymentFrequency;
        uint256 startingDate;
        uint256 maturityDate;
        uint256 benchmark;
    }
}

